mapper.select:
select
  id, name, sex, age, number, classes, state, address, phone
from student_info


        and id = #{id}


        and name = #{name}


        and sex = #{sex}


        and age = #{age}


        and number = #{number}


        and class = #{class}


        and state = #{state}


        and address = #{address}


        and phone = #{phone}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from student_info


        and id = #{id}


        and name = #{name}


        and sex = #{sex}


        and age = #{age}


        and number = #{number}


        and class = #{class}


        and state = #{state}


        and address = #{address}


        and phone = #{phone}
